// src/App.jsx
import React, { useState, useEffect } from 'react';
import { fetchResults, fetchStats } from './api';
import TimeSeriesChart from './components/TimeSeriesChart';
import TopList         from './TopList';

function App() {
  const [results, setResults] = useState([]);
  const [stats, setStats]     = useState(null);
  const [error, setError]     = useState(null);

  useEffect(() => {
    const load = async () => {
      try {
        const [r, s] = await Promise.all([
          fetchResults(5),     // just grab 5 for now
          fetchStats(15)       // last 15 minutes
        ]);
        setResults(r);
        setStats(s);
      } catch (e) {
        setError(e.message);
      }
    };
    load();
    const iv = setInterval(load, 30000); // refresh every 30s
    return () => clearInterval(iv);
  }, []);

  if (error) {
    return <div style={{ padding: 20, color: 'red' }}>
      ‚ùå {error}
    </div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>üî¥ Reddit r/news Sentiment Dashboard</h1>

      {stats ? (
        <section>
          <h2>Stats (last {stats.window_minutes} min)</h2>
          <ul>
            <li>Average sentiment: {stats.average_sentiment.toFixed(2)}</li>
            <li>Positive: {stats.positive_count}</li>
            <li>Negative: {stats.negative_count}</li>
            <li>Total: {stats.total_count}</li>
          </ul>
        </section>
      ) : (
        <p>Loading stats‚Ä¶</p>
      )}

      {results.length > 0 ? (
        <section>
          <h2>Latest Posts</h2>
          <ul>
            {results.map(item => (
              <li key={item.id}>
                <a href={item.url} target="_blank" rel="noopener noreferrer">
                  {item.title}
                </a>
                <strong style={{ marginLeft: 8 }}>
                  {item.label} ({(item.score*100).toFixed(0)}%)
                </strong>
              </li>
            ))}
          </ul>
        </section>
      ) : (
        <p>Loading posts‚Ä¶</p>
      )}
    </div>
  );
}

export default App;
