import React, { useEffect, useState } from 'react';
import { fetchResults, fetchStats } from './api';
import TimeSeriesChart from './components/TimeSeriesChart';
import TopList from './components/TopList';

export default function App() {
  const [results, setResults] = useState([]);
  const [stats, setStats] = useState({});

  useEffect(() => {
    const loadData = async () => {
      setResults(await fetchResults(50));
      setStats(await fetchStats(60));
    };
    loadData();
    const interval = setInterval(loadData, 30000);
    return () => clearInterval(interval);
  }, []);

  return (
    <div className="p-4">
      <h1 className="text-xl font-bold mb-4">Reddit r/news Sentiment</h1>
      <TimeSeriesChart data={stats.timeline || []} />
      <div className="flex gap-4 mt-6">
        <TopList title="Top Positive" items={results.filter(i => i.label === 'POSITIVE')} />
        <TopList title="Top Negative" items={results.filter(i => i.label === 'NEGATIVE')} />
      </div>
    </div>
  );
}
